import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import NavBar from '../../components/NavBar.tsx';
import './Success.css';
import successIcon from '../../assets/images/successicon.svg';
import backIcon from '../../assets/images/back-button.svg';
import backIconHover from '../../assets/images/back-button-hover.svg';
import nextIcon from '../../assets/images/next-button.svg';
import nextIconHover from '../../assets/images/next-button-hover.svg';
import confetti from 'canvas-confetti';

const SuccessPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { name, gender, age, contact, contactType } = location.state || {};

  const [backHover, setBackHover] = useState(false);
  const [nextHover, setNextHover] = useState(false);

  // Í∏ÄÍ∑Ä Ï†ÑÌôò ÏÉÅÌÉú
  const [step, setStep] = useState(1);

  useEffect(() => {
    confetti({
      particleCount: 200,
      spread: 100,
      origin: { y: 0.6 },
      zIndex: 1000
    });
  }, []);

  // 5Ï¥à ÌõÑ Í∏ÄÍ∑Ä Ï†ÑÌôò
  useEffect(() => {
    const timer = setTimeout(() => {
      setStep(2);
    }, 5000);
    return () => clearTimeout(timer);
  }, []);

  const handleStartWorldcup = () => {
    navigate('/idealworldcup', {
      state: { name, gender, age, contact, contactType }
    });
  };

  const handleGoHome = () => {
    navigate('/');
  };

  return (
    <div className="page-wrapper">
      <NavBar />

      <div className="success-container">
        <img
          src={successIcon}
          alt="ÏÑ±Í≥µ"
          className="success-icon animate"
        />

        {step === 1 ? (
          <>
            <h3 className="fade-in delay-1 red-text">üéâ ÏñºÍµ¥ Ïù∏Ïãù ÏôÑÎ£å</h3>
            <h1 className="fade-in delay-2 white-text">{name}Îãò, ÌôòÏòÅÌï¥Ïöî</h1>
          </>
        ) : (
          <h2 className="fade-in white-text new-message">
            Ïû†Ïãú ÌõÑ Ïù¥ÏÉÅÌòï ÏõîÎìúÏªµÏù¥ ÏãúÏûëÎê©ÎãàÎã§
          </h2>
        )}

        <div className="button-group fade-in delay-3">
          <img
            src={backHover ? backIconHover : backIcon}
            className="svg-button"
            onClick={handleGoHome}
            alt="Ï≤òÏùåÏúºÎ°ú"
            onMouseEnter={() => setBackHover(true)}
            onMouseLeave={() => setBackHover(false)}
          />
          <img
            src={nextHover ? nextIconHover : nextIcon}
            className="svg-button"
            onClick={handleStartWorldcup}
            alt="ÏõîÎìúÏªµ ÏãúÏûë"
            onMouseEnter={() => setNextHover(true)}
            onMouseLeave={() => setNextHover(false)}
          />
        </div>
      </div>
    </div>
  );
};

export default SuccessPage;
