// src/pages/IdealWorldcupPage.tsx
import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import IdealMatchFrame from '../../components/IdealMatchFrame';

type Candidate = {
  name: string;
  image: string;
};

const allMaleCandidates: Candidate[] = [...Array(100)].map((_, i) => ({
  name: `남자 연예인 ${i + 1}`,
  image: `/images/male/male${i + 1}.jpg`,
}));

const allFemaleCandidates: Candidate[] = [...Array(100)].map((_, i) => ({
  name: `여자 연예인 ${i + 1}`,
  image: `/images/female/female${i + 1}.jpg`,
}));

const shuffle = (array: Candidate[]) =>
  array.sort(() => Math.random() - 0.5);

const IdealWorldcupPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { name, gender, age, contact, contactType } = location.state || {};
  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [round, setRound] = useState(0);
  const [winners, setWinners] = useState<Candidate[]>([]);

  // 확인 로그
  useEffect(() => {
    console.log("넘어온 정보", name, gender, age, contact, contactType);
  }, []);

  
  useEffect(() => {
    const opponentGender = gender === 'female' ? 'male' : 'female';
    const initialCandidates =
    opponentGender === 'male' ? shuffle(allMaleCandidates) : shuffle(allFemaleCandidates);
    setCandidates(initialCandidates.slice(0, 64));
  }, [gender]);

  const handleSelect = (winner: Candidate) => {
    const nextWinners = [...winners, winner];
    const nextRound = round + 2;

    if (nextRound >= candidates.length) {
      if (nextWinners.length === 1) {
        navigate('/final', { state: { winner: nextWinners[0] } });
      } else {
        setCandidates(nextWinners);
        setWinners([]);
        setRound(0);
      }
    } else {
      setWinners(nextWinners);
      setRound(nextRound);
    }
  };

  if (candidates.length < 2) return <div>Loading...</div>;

  const left = candidates[round];
  const right = candidates[round + 1];

  return (
    <div>
      <h2>이상형 월드컵</h2>
      <IdealMatchFrame left={left} right={right} onSelect={handleSelect} />
      <p>{candidates.length}강 - {round / 2 + 1}번째 대결</p>
    </div>
  );
};

export default IdealWorldcupPage;
