import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import NavBar from '../../components/NavBar.tsx';

import backArrow from '../../assets/images/back-arrow.svg';
import vsIcon from '../../assets/images/vsicon.svg';
import pickIcon from '../../assets/images/pick.svg';
import pickHoverIcon from '../../assets/images/pick-hover.svg';

import './IdealWorldcupPage.css';

type Candidate = {
  name: string;
  image: string;
};

const maleCandidates: Candidate[] = [
  { name: '송강', image: '/images/male/송강.png' },
  { name: '변우석', image: '/images/male/변우석.png' },
  { name: '김영광', image: '/images/male/김영광.png' },
  { name: '김선호', image: '/images/male/김선호.png' },
  { name: '정해인', image: '/images/female/정해인.png' },
  { name: '박보검', image: '/images/female/박보검.png' },
];

const femaleCandidates: Candidate[] = [
  { name: '아이유', image: '/images/female/아이유.png' },
  { name: '장원영', image: '/images/female/장원영.png' },
  { name: '카리나', image: '/images/female/카리나.png' },
  { name: '박보영', image: '/images/female/박보영.png' },
  { name: '한소희', image: '/images/female/한소희.png' },
  { name: '설윤', image: '/images/female/설윤.png' },
];

const shuffle = (array: Candidate[]) => [...array].sort(() => Math.random() - 0.5);

const IdealWorldcupPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { name, gender, age, contact, contactType } = location.state || {};

  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [round, setRound] = useState(0);
  const [winners, setWinners] = useState<Candidate[]>([]);
  const [pickedIndex, setPickedIndex] = useState<number | null>(null); // 현재 선택된 후보 인덱스

  useEffect(() => {
    const opponentGender = gender === 'female' ? 'male' : 'female';
    const nameList = opponentGender === 'male' ? maleCandidates : femaleCandidates;
    const shuffled = shuffle(nameList);
    setCandidates(shuffled.slice(0, 4)); // 테스트용 4명
    setRound(0);
    setWinners([]);
    setPickedIndex(null);
  }, [gender]);

  const handleSelect = (winner: Candidate) => {
    const nextWinners = [...winners, winner];
    const nextRound = round + 2;

    if (nextRound >= candidates.length) {
      if (nextWinners.length === 1) {
        navigate('/final', {
          state: {
            winner: nextWinners[0],
            name,
            gender,
            age,
            contact,
            contactType,
            idealTypeCelebrity: nextWinners[0].name,
          },
        });
      } else {
        setCandidates(nextWinners);
        setWinners([]);
        setRound(0);
      }
    } else {
      setWinners(nextWinners);
      setRound(nextRound);
    }
    setPickedIndex(null);
  };

  const handlePick = (index: number) => {
    if (pickedIndex === index) {
      setPickedIndex(null); // 다시 선택 해제 가능
    } else {
      setPickedIndex(index);
    }
  };

  const handleBackClick = () => {
    if (round > 0) {
      // 이전 라운드로 돌아가기
      setRound(round - 2);
      setPickedIndex(null);
      if (winners.length > 0) {
        setWinners(winners.slice(0, -1));
      }
    } else {
      navigate(-1);
    }
  };

  if (candidates.length < 2) return <div>Loading...</div>;

  const left = candidates[round];
  const right = candidates[round + 1];

  return (
    <>
      <NavBar />
      <div className="worldcup-wrapper">
        {/* 라운드 표시 */}
        <div className="round-indicator" aria-label="현재 라운드">
          <span className="total-round">{candidates.length}강</span>
          <span className="current-match">{Math.floor(round / 2) + 1}번째 대결</span>
        </div>

        <div className="match-area">
          {/* back 버튼 */}
          <button
            className="icon-button back-button"
            onClick={handleBackClick}
            aria-label="이전 단계"
          >
            <img src={backArrow} alt="이전 단계" />
          </button>

          {/* 왼쪽 후보 */}
          <div
            className={`candidate-card ${pickedIndex === 0 ? 'picked' : ''}`}
            onClick={() => handlePick(0)}
          >
            <img
              src={left.image}
              alt={left.name}
              className="candidate-image"
              draggable={false}
            />
            {/* 빨간 오버레이: picked일때만 보임 */}
            {pickedIndex === 0 && <div className="overlay-red" />}
            {/* pick-hover 아이콘 */}
            {pickedIndex === 0 && (
              <img src={pickHoverIcon} alt="Picked" className="pick-hover-icon" />
            )}

            {/* 이름과 pick 버튼 */}
            <div className="candidate-info">
              <div className="candidate-name">{left.name}</div>
              <button
                className="pick-button"
                onClick={e => {
                  e.stopPropagation();
                  handlePick(0);
                }}
                aria-pressed={pickedIndex === 0}
                aria-label={`${left.name} 선택하기`}
              >
                <img src={pickIcon} alt="Pick" />
                <span className="pick-text">pick!</span>
              </button>
            </div>
          </div>

          {/* vs 아이콘 */}
          <img src={vsIcon} alt="VS" className="vs-icon" />

          {/* 오른쪽 후보 */}
          <div
            className={`candidate-card ${pickedIndex === 1 ? 'picked' : ''}`}
            onClick={() => handlePick(1)}
          >
            <img
              src={right.image}
              alt={right.name}
              className="candidate-image"
              draggable={false}
            />
            {/* 빨간 오버레이 */}
            {pickedIndex === 1 && <div className="overlay-red" />}
            {/* pick-hover 아이콘 */}
            {pickedIndex === 1 && (
              <img src={pickHoverIcon} alt="Picked" className="pick-hover-icon" />
            )}

            {/* 이름과 pick 버튼 */}
            <div className="candidate-info">
              <div className="candidate-name">{right.name}</div>
              <button
                className="pick-button"
                onClick={e => {
                  e.stopPropagation();
                  handlePick(1);
                }}
                aria-pressed={pickedIndex === 1}
                aria-label={`${right.name} 선택하기`}
              >
                <img src={pickIcon} alt="Pick" />
                <span className="pick-text">pick!</span>
              </button>
            </div>
          </div>

          {/* next 버튼 */}
          <button
            className="icon-button next-button"
            onClick={() => {
              if (pickedIndex === null) {
                alert('후보 중 하나를 선택해 주세요!');
                return;
              }
              const winner = pickedIndex === 0 ? left : right;
              handleSelect(winner);
              setPickedIndex(null);
            }}
            aria-label="다음 단계"
          >
            <img src={backArrow} alt="다음 단계" />
          </button>
        </div>
      </div>
    </>
  );
};

export default IdealWorldcupPage;
