        .then((res) => res.json())
        .then((data) => console.log('‚úîÔ∏è Ïù¥ÏÉÅÌòï Ïó∞ÏòàÏù∏ Ï†ÄÏû• ÏôÑÎ£å:', data))
        .catch((err) => console.error('‚ùå Ï†ÄÏû• Ïã§Ìå®:', err));
    }

    // 10Ï¥à ÌõÑ Ïù¥Îèô
    const timer = setTimeout(() => {
      navigate('/finalmessage');
    }, 10000);

    // Î°úÎî©Î∞î ÏßÑÌñâ
    const interval = setInterval(() => {
      setProgress((prev) => (prev >= 100 ? 100 : prev + 1));
    }, 100); // 10Ï¥à ÎèôÏïà 0 ‚Üí 100%

    return () => {
      clearTimeout(timer);
      clearInterval(interval);
    };
  }, [navigate, winner]);

  if (!winner) {
    return <div className="container">ÏÑ†ÌÉùÎêú Ïù¥ÏÉÅÌòï Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.</div>;
  }

  return (
    <>
      <NavBar />
      <div className="final-container">
        <h1 className="final-title">üéâ ÏµúÏ¢Ö ÏÑ†ÌÉù: {winner.name} üéâ</h1>

        <div className="image-wrapper">
          <img src={winner.image} alt={winner.name} className="final-image animate-pop" />
          <img src={crownIcon} alt="ÏôïÍ¥Ä" className="crown-icon" />
          <img src={sparkleGif} alt="Î∞òÏßùÏù¥" className="sparkle" />
        </div>

        <div className="progress-bar-wrapper">
          <div className="progress-bar" style={{ width: `${progress}%` }} />
        </div>
        <p className="loading-text">ÎßàÏßÄÎßâ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô Ï§ë...</p>
      </div>
    </>
  );
};

export default FinalPage;
