import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import NavBar from '../../components/NavBar.tsx';
import './IdealWorldcupPage.css';

type Candidate = {
  name: string;
  image: string;
};

const maleCandidates: Candidate[] = [
  { name: '송강', image: '/images/male/송강.png' },
  { name: '변우석', image: '/images/male/변우석.png' },
  { name: '김영광', image: '/images/male/김영광.png' },
  { name: '김선호', image: '/images/male/김선호.png' },
  { name: '정해인', image: '/images/female/정해인.png' },
  { name: '박보검', image: '/images/female/박보검.png' },
];
const femaleCandidates: Candidate[] = [
  { name: '아이유', image: '/images/female/아이유.png' },
  { name: '장원영', image: '/images/female/장원영.png' },
  { name: '카리나', image: '/images/female/카리나.png' },
  { name: '박보영', image: '/images/female/박보영.png' },
  { name: '한소희', image: '/images/female/한소희.png' },
  { name: '설윤', image: '/images/female/설윤.png' },
];

const shuffle = (array: Candidate[]) => [...array].sort(() => Math.random() - 0.5);

const backIcon = '/images/icons/back-arrow.svg';  // 백아이콘 경로
const vsIcon = '/images/icons/vsicon.svg';        // vs 아이콘 경로
const pickIcon = '/images/icons/pick.svg';        // pick 아이콘 경로
const pickHoverIcon = '/images/icons/pick-hover.svg'; // pick hover 아이콘 경로

const IdealWorldcupPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { name, gender, age, contact, contactType } = location.state || {};

  const [candidates, setCandidates] = useState<Candidate[]>([]);
  const [round, setRound] = useState(0);
  const [winners, setWinners] = useState<Candidate[]>([]);
  const [pickedIndex, setPickedIndex] = useState<number | null>(null);
  const [historyRounds, setHistoryRounds] = useState<number[]>([]);

  useEffect(() => {
    const opponentGender = gender === 'female' ? 'male' : 'female';
    const nameList = opponentGender === 'male' ? maleCandidates : femaleCandidates;
    const shuffled = shuffle(nameList);
    setCandidates(shuffled.slice(0, 4)); // 4명으로 테스트
    setRound(0);
    setWinners([]);
    setPickedIndex(null);
    setHistoryRounds([]);
  }, [gender]);

  const handlePick = (index: number) => {
    setPickedIndex(index);
  };

  const handleConfirmPick = () => {
    if (pickedIndex === null) {
      alert('하나를 선택해주세요.');
      return;
    }
    const winner = round + pickedIndex;
    const nextWinners = [...winners, candidates[winner]];
    const nextRound = round + 2;

    setHistoryRounds((prev) => [...prev, round]); // 현재 라운드 히스토리에 저장

    if (nextRound >= candidates.length) {
      if (nextWinners.length === 1) {
        navigate('/final', {
          state: {
            winner: nextWinners[0],
            name,
            gender,
            age,
            contact,
            contactType,
            idealTypeCelebrity: nextWinners[0].name,
          },
        });
      } else {
        setCandidates(nextWinners);
        setWinners([]);
        setRound(0);
      }
    } else {
      setWinners(nextWinners);
      setRound(nextRound);
    }
    setPickedIndex(null);
  };

  const handleBack = () => {
    if (historyRounds.length === 0) {
      navigate(-1); // 이전 페이지 없으면 뒤로 가기 시도
    } else {
      // 이전 라운드 복원
      const prevRounds = [...historyRounds];
      const prevRound = prevRounds.pop()!;
      setHistoryRounds(prevRounds);
      setRound(prevRound);
      setWinners((w) => w.slice(0, w.length - 1));
      setPickedIndex(null);
    }
  };

  if (candidates.length < 2) return <div>Loading...</div>;

  const left = candidates[round];
  const right = candidates[round + 1];

  // 라운드 텍스트
  const totalRounds = candidates.length;
  const currentMatch = Math.floor(round / 2) + 1;

  return (
    <>
      <NavBar />
      <div className="worldcup-wrapper">
        {/* 라운드 표시 */}
        <div className="round-indicator">
          <span className="total-round">{totalRounds}강</span> / <span className="current-match">{currentMatch}번째 대결</span>
        </div>

        {/* back - 이미지1 - vs - 이미지2 - next 버튼 */}
        <div className="match-area">

          <button className="icon-button back-button" onClick={handleBack} aria-label="이전">
            <img src={backIcon} alt="back" />
          </button>

          <div
            className={`candidate-card ${pickedIndex === 0 ? 'picked' : ''}`}
            onClick={() => handlePick(0)}
          >
            <img
              src={left.image}
              alt={left.name}
              className="candidate-image"
              draggable={false}
            />
            <div className="overlay-red" style={{ display: pickedIndex === 0 ? 'block' : 'none' }} />
            {pickedIndex === 0 && (
              <img src={pickHoverIcon} alt="pick hover" className="pick-hover-icon" />
            )}
            <div className="candidate-info">
              <span className="candidate-name">{left.name}</span>
              <button
                className="pick-button"
                onClick={(e) => {
                  e.stopPropagation();
                  handlePick(0);
                }}
              >
                <img src={pickIcon} alt="pick" />
                <span className="pick-text">pick!</span>
              </button>
            </div>
          </div>

          <img src={vsIcon} alt="vs" className="vs-icon" />

          <div
            className={`candidate-card ${pickedIndex === 1 ? 'picked' : ''}`}
            onClick={() => handlePick(1)}
          >
            <img
              src={right.image}
              alt={right.name}
              className="candidate-image"
              draggable={false}
            />
            <div className="overlay-red" style={{ display: pickedIndex === 1 ? 'block' : 'none' }} />
            {pickedIndex === 1 && (
              <img src={pickHoverIcon} alt="pick hover" className="pick-hover-icon" />
            )}
            <div className="candidate-info">
              <span className="candidate-name">{right.name}</span>
              <button
                className="pick-button"
                onClick={(e) => {
                  e.stopPropagation();
                  handlePick(1);
                }}
              >
                <img src={pickIcon} alt="pick" />
                <span className="pick-text">pick!</span>
              </button>
            </div>
          </div>

          <button className="icon-button next-button" onClick={handleConfirmPick} aria-label="다음">
            <img src={backIcon} alt="next" />
          </button>

        </div>
      </div>
    </>
  );
};

export default IdealWorldcupPage;
