import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './FinalMessagePage.css';
import NavBar from '../../components/NavBar.tsx';

import LetterOuter from '../../assets/images/letterouter.svg';
import LetterInner from '../../assets/images/letterinner.svg';
import Flap from '../../assets/flap.svg'; // 삼각형 flap svg

const FinalMessagePage = () => {
  const navigate = useNavigate();
  const [opened, setOpened] = useState(false);

  useEffect(() => {
    const timer = setTimeout(() => {
      setOpened(true);
    }, 1000);
    return () => clearTimeout(timer);
  }, []);

  return (
    <>
      <NavBar />
      <div className="final-message-page">
        <div className="envelope-container">
          <img src={LetterOuter} alt="Envelope Outer" className="envelope-outer" />
          <img
            src={LetterInner}
            alt="Letter Inner"
            className={`letter-inner ${opened ? 'slide-up' : ''}`}
          />
          <img
            src={Flap}
            alt="Envelope Flap"
            className={`envelope-flap ${opened ? 'open' : ''}`}
          />
        </div>
        <button className="home-button" onClick={() => navigate('/')}>
          처음으로
        </button>
      </div>
    </>
  );
};

export default FinalMessagePage;
