import React, { useEffect, useState } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import './FinalPage.css';
import NavBar from '../../components/NavBar.tsx';
import sparkle from '../../assets/images/sparkle.svg'; // SVG ÏßÅÏ†ë import

const FinalPage = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { winner } = location.state || {};
  const [progress, setProgress] = useState(0);

  useEffect(() => {
    if (winner?.name) {
      fetch('http://127.0.0.1:5000/set_ideal', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          idealTypeCelebrity: winner.name,
        }),
      })
        .then((res) => res.json())
        .then((data) => console.log('‚úîÔ∏è Ïù¥ÏÉÅÌòï Ïó∞ÏòàÏù∏ Ï†ÄÏû• ÏôÑÎ£å:', data))
        .catch((err) => console.error('‚ùå Ï†ÄÏû• Ïã§Ìå®:', err));
    }

    const timer = setTimeout(() => {
      navigate('/finalmessage');
    }, 10000);

    const interval = setInterval(() => {
      setProgress((prev) => (prev >= 100 ? 100 : prev + 1));
    }, 100);

    return () => {
      clearTimeout(timer);
      clearInterval(interval);
    };
  }, [navigate, winner]);

  if (!winner) {
    return <div>ÏÑ†ÌÉùÎêú Ïù¥ÏÉÅÌòï Ï†ïÎ≥¥Í∞Ä ÏóÜÏäµÎãàÎã§.</div>;
  }

  return (
    <>
      <NavBar />
      <div className="final-container">
        <h1 className="final-title">üéâ ÏµúÏ¢Ö {winner.name}ÏùÑ(Î•º) ÏÑ†ÌÉùÌïòÏÖ®ÏäµÎãàÎã§! üéâ</h1>

        <div className="final-image-wrapper">
          <img src={sparkle} alt="Sparkle" className="sparkle sparkle-1" />
          <img src={sparkle} alt="Sparkle" className="sparkle sparkle-2" />
          <img src={winner.image} alt={winner.name} className="final-image" />
          <img src={sparkle} alt="Sparkle" className="sparkle sparkle-3" />
          <img src={sparkle} alt="Sparkle" className="sparkle sparkle-4" />
          <img src={sparkle} alt="Sparkle" className="sparkle sparkle-5" />
          
        </div>

        <div className="progress-bar-wrapper">
          <div className="progress-bar" style={{ width: `${progress}%` }} />
        </div>
        <p className="loading-text">ÎßàÏßÄÎßâ ÌéòÏù¥ÏßÄÎ°ú Ïù¥Îèô Ï§ë...</p>
      </div>
    </>
  );
};

export default FinalPage;
